package webDriverPackage;

public class DoWhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	/*
	 Main difference between the do while and while is: DO, will Execute first and then check the condition
	 DO/WHILE -- Less frequent	 
	*/
		
		int x=0; 
		
		do 
		{
			System.out.println(x);
			x = x+1;
		}
		
		while (x < 20);
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
